'use strict';

var builder = require('lui-build');

module.exports = function(grunt) {
	grunt.registerMultiTask('lui-build', 'Build your custom lucca-ui distribution', function() {
		var options = this.options(
			{
				basePath: 'c:/sites/lucca-ui/',
				customThemeBasePath: null,
				build: 'scss/themes/default/build.json',
				dest: 'build/lucca-ui.build.scss',
			}
		);

		if (builder.build(options))
			grunt.log.writeln('Compiled theme! -> ' + options.dest.cyan);
		else
			grunt.log.writeln('Error encountered while building your theme.'['red'].bold);
	});
}
